package java_batch_weekend_september20;

public class Datatyps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int myInt = 100;
        char myChar = 'A';
        boolean myBoolean = true;
        short myShort = 12342;
        float myFloat = 12.08f;
        double myDouble = 3.141256;
        long myLong = 900000000000000L;
        byte myByte = 10;
        System.out.println("This myInt datatype:"  + myInt);
        System.out.println("This myChar datatype:"  + myChar);
        System.out.println("This myBoolean datatype:"  + myBoolean);
        System.out.println("This myShort datatype:"  + myShort);
        System.out.println("This myFloat datatype:" + myFloat);
        System.out.println("This myDouble datatype:" + myDouble);
        System.out.println("This myLong datatype:" + myLong);
        System.out.println("This myByte datatype:" + myByte);
	}

}
