package java_batch_weekend_september20;

public class nestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  // number of columns

		for (int i = 1; i<=5; i++) {
		      
		    for (int j = 0; j<i; j++)
		    {
		        System.out.print('*');
		}
		    System.out.println();
		}
	}
}


